const {useState, useEffect} = React;



function useFormInput(iv,e) {
  const [v,setV] = useState(iv);
  useEffect(e || (x => x),[v]);
  return {
    value: v,
    onChange: e => setV(e.target.value)
  }
}

function Foo() {


  let [msg,setMsg] = useState('ok')

  let total = useFormInput(21)
  let marquee = useFormInput('wee',() => setMsg(Math.random()))
  return (
    <div>
      <h1>React stuff</h1>
      <marquee>{marquee.value}</marquee>
      Marquee text 
      <input {...marquee} /><br />
      Total
      <input {...total} />
      <div>{total.value}</div>
      <span>{msg}</span>
    </div>
  )

}
class App extends React.Component {
  state = {
    count: 0
  }

  inc = () => this.setState({
    count: this.state.count + 1
  })

  dec = () => this.setState({
    count: this.state.count - 1
  })

  render() {
    return (
      <div>
        <h2>{ this.state.count }</h2>
        <button onClick={this.inc}>Increment</button>
        <button onClick={this.dec}>Decrement</button>
        <Foo />
      </div>
    )
  }
}

ReactDOM.render(<App />, document.getElementById('app'))
